import cv2from open3d import *import numpy as npimport oslidar_bin_dir="/home/kangning/Documents/Masterarbeit/frustum-pointnets/dataset/KITTI/object/training/velodyne/"lidar_bin_paths=[]bin_names= os.listdir(lidar_bin_dir)for index,lidar_name in enumerate(bin_names):    sequence=lidar_name.split(".bin")[0]    lidar_bin_path= lidar_bin_dir+sequence+".bin"    point_cloud = np.fromfile(lidar_bin_path, dtype=np.float32).reshape(-1, 4)    point_cloud_xyz = point_cloud[:, 0:3]    pcd = open3d.PointCloud()    pcd.points = open3d.Vector3dVector(point_cloud_xyz)    vis = open3d.Visualizer()    vis.create_window()    ctr = vis.get_view_control()    vis.add_geometry(pcd)    vis.run()    param = ctr.convert_to_pinhole_camera_parameters()    camera_trajectory = PinholeCameraTrajectory()    camera_trajectory.intrinsic = param[0]    camera_trajectory.extrinsic = Matrix4dVector([param[1]])    output_path = "/home/kangning/Desktop/camera_trajectory/"    write_pinhole_camera_trajectory(os.path.join(output_path, sequence + ".json" ), camera_trajectory)    vis.destroy_window()